import { createSlice } from '@reduxjs/toolkit';

const initialState = {
  threadId: '',
  initialResponse: '',
};

const threadSlice = createSlice({
  name: 'thread',
  initialState,
  reducers: {
    setThreadId(state, action) {
      state.threadId = action.payload;
    },
    setInitialResponse(state, action) {
      state.initialResponse = action.payload;
    },
    clearThreadId(state) {
      state.threadId = '';
    },
  },
});

export const { setThreadId, setInitialResponse, clearThreadId } = threadSlice.actions;
export default threadSlice.reducer;
