import { createSlice } from "@reduxjs/toolkit";

const initialState = {
  questions: [],
  answers: {},
  valueProposition: "",
};

const questionsSlice = createSlice({
  name: "questions",
  initialState,
  reducers: {
    setQuestions: (state, action) => {
      state.questions = action.payload; // Guardar preguntas
    },
    setAnswer: (state, action) => {
      const { questionId, answer } = action.payload;
      state.answers[questionId] = answer; // Guardar respuesta por ID de pregunta
    },
    setValueProposition: (state, action) => {
      state.valueProposition = action.payload; // Guardar promesa de valor
    },
  },
});

export const { setQuestions, setAnswer, setValueProposition } = questionsSlice.actions;

export default questionsSlice.reducer;
