import axios from 'axios';

const bffLeadClient = axios.create({
  baseURL: process.env.REACT_APP_API_BFF_LEAD,
  headers: {
    'Content-Type': 'application/json',
  },
});

bffLeadClient.interceptors.request.use((config) => {
    const token = localStorage.getItem('authToken');
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
    }
    return config;
  });

  
  export const registerUser = async (name, email, passcode = null) => {
    try {
      const organizationId = process.env.REACT_APP_ORGANIZATION_ID; // Obtén el Organization_id desde .env
  
      const requestBody = {
        name,
        email,
        organization_id: organizationId,
        ...(passcode && { passcode }), // Agrega passcode solo si existe
      };
  
      const response = await bffLeadClient.post('/lead_auth', requestBody);
      return response.data; // Devuelve el token y otros datos relevantes
    } catch (error) {
      console.error('Error en registerUser:', error);
      throw error;
    }
  };
  
  
  export default bffLeadClient;
